import syssys.path.append('..')from multiprocessing import Processimport FunctionCommon as Fundef run(fun, separately, *args):    """    :param separately: run function separately    :param fun:function to run    :type args: object parameters    # example1:    args = ([1, 2, 3], "23232")    MultiProcess.run(main_process, True, *args)    # example2:    args = ([1, 2, 3])    MultiProcess.run(main_process, False, *args)    # example3:    MultiProcess.run(main_process, False, 1,2,3)    """    process_record = []    devices = Fun.devices_list_with_connection_check()    for i in range(len(devices)):        params = [devices[i]]        if separately:            params.append(args[i])        else:            params.append(args)        p = Process(target=fun, args=params)        p.start()        process_record.append(p)    for p in process_record:        p.join()