import syssys.path.append('..')import FunctionCommon as Funfrom monkey import MonkeyThreadfrom multiprocessing import Process# from multiprocessing import Pool# from multiprocessing.dummy import Pool as ThreadPooldef main_process(dev):    Fun.log(dev + 'start')    Fun.log(dev + 'sleep:')    Fun.sleep(5)    Fun.log(dev + 'end')def works(fun, devices):    process_record = []    # pool = ThreadPool(len(devices))    # results = map(devices)    # results = pool.map(MonkeyThread.monkey_work.run, devices)    # pool.close()    # pool.join()    for dev in devices:        p = Process(target=fun, args=(dev,))        p.start()        process_record.append(p)    for p in process_record:        p.join()if __name__ == '__main__':    processes = ['1', '2', '3', '4', '5']    works(main_process, processes)