import syssys.path.append('..')import FunctionCommon as FunComimport FunctionPackage as FunPkgpkg_name = 'org.thisisafactory.simiasque'activity_name = ' org.thisisafactory.simiasque/.MyActivity_'apk_file_keyword = 'status_bar_helper.apk'turn_on_shell = ' shell am broadcast -a org.thisisafactory.simiasque.SET_OVERLAY --ez enable true'turn_off_shell = ' shell am broadcast -a org.thisisafactory.simiasque.SET_OVERLAY --ez enable false'def start_status_bar_cover(tag, replace_apk=False):    if replace_apk or not FunCom.tell_app_installed(tag, pkg_name):        apk_path = ''        for f in FunCom.list_dir(FunCom.Res.util_path):            if apk_file_keyword in f:                apk_path = FunCom.get_abspath(FunCom.path_join(FunCom.Res.util_path, f))        if apk_path == '':            FunCom.raise_error('status bar Apk file not found under ./util directory')        FunPkg.install_r(tag, apk_path, True)        FunCom.sleep(6)    FunCom.p_open(FunCom.Res.asb_shell_start_activity(tag, activity_name))    FunCom.send_key_back(tag)    FunCom.sleep(3)    switch_cover(tag)def switch_cover(tag, turn_on=True):    FunCom.p_open(FunCom.Res.adb_s_tag_prefix(tag) + (turn_on_shell if turn_on else turn_off_shell))